            <Slider
              value={preferences.duration}
              onValueChange={(value) => setPreferences(prev => ({ ...prev, duration: value }))}
              max={21}
              min={1}
              step={1}
              className="w-full"
              data-testid="slider-duration"
            />
            <div className="flex justify-between text-sm text-muted-foreground">
              <span>1 day</span>
              <span>21+ days</span>
            </div>
          </div>

          {/* Group Size */}
